    const [run, setRun] = useState(true);

    const timer = useRef()

    return (
        <div className='stopwatch'>
            <p className='timer'>{formatTime(time)}</p>
            <div className='actions'>
                <button>Restart</button>
                <button>Stop</button>
            </div>
        </div>
    );
}
